function initDashboard(){console.log('Initializing FX Trading Bot Dashboard...');loadLiveData();setupAutoRefresh();setupEventListeners();console.log('Dashboard initialized successfully');} function setupAutoRefresh(){setInterval(()=>{const activeTab=getActiveTab(); if (activeTab){loadTabData(activeTab);}},DASHBOARD_CONFIG.REFRESH_INTERVAL);} function setupEventListeners(){const strategyTf=document.getElementById('strategyTimeframe'); if (strategyTf){strategyTf.addEventListener('change',debounce(loadStrategyComparison,300));}const pairTf=document.getElementById('pairTimeframe'); if (pairTf){pairTf.addEventListener('change',debounce(loadPairComparison,300));}const matrixTf=document.getElementById('matrixTimeframe'); if (matrixTf){matrixTf.addEventListener('change',debounce(loadPerformanceMatrix,300));}console.log('Event listeners attached');} function manualRefresh(){const activeTab=getActiveTab(); if (activeTab){loadTabData(activeTab);}} if (document.readyState==='loading'){document.addEventListener('DOMContentLoaded',initDashboard);} else {initDashboard();}