function switchTab(tabName){document.querySelectorAll('.tab-content').forEach(tab=>{tab.classList.remove('active');});document.querySelectorAll('.tab-btn').forEach(btn=>{btn.classList.remove('active');});const selectedTab=document.getElementById(tabName); if (selectedTab){selectedTab.classList.add('active');}const activeBtn=event?.target; if (activeBtn){activeBtn.classList.add('active');}loadTabData(tabName);} function loadTabData(tabName){ switch (tabName){ case 'live':loadLiveData();break; case 'backtest':loadBacktestData();break; case 'strategies':loadStrategyComparison();break; case 'pairs':loadPairComparison();break; case 'matrix':loadPerformanceMatrix();break;default:console.warn(`Unknown tab:${tabName}`);}} function getActiveTab(){const activeContent=document.querySelector('.tab-content.active'); return activeContent ? activeContent.id:null;} function switchTabProgrammatic(tabName){const tab=document.getElementById(tabName);const btn=document.querySelector(`[onclick="switchTab('${tabName}')"]`); if (tab&&btn){document.querySelectorAll('.tab-content').forEach(t=>t.classList.remove('active'));document.querySelectorAll('.tab-btn').forEach(b=>b.classList.remove('active'));tab.classList.add('active');btn.classList.add('active');loadTabData(tabName);}}