function debounce(func,delay){let timeoutId; return function debounced(...args){clearTimeout(timeoutId);timeoutId=setTimeout(()=>{func.apply(this,args);},delay);};} function throttle(func,interval){let lastCall=0; return function throttled(...args){const now=Date.now(); if (now-lastCall>=interval){func.apply(this,args);lastCall=now;}};} function formatPercent(value,decimals=2){const sign=value>=0 ? '+':''; return `${sign}${(value*100).toFixed(decimals)}%`;} function formatCurrency(value,symbol='$',decimals=2){const sign=value>=0 ? '':'-'; return `${sign}${symbol}${Math.abs(value).toFixed(decimals)}`;} function formatNumber(value,decimals=2){ return (value||0).toFixed(decimals);} function formatDateTime(dateStr){const date=typeof dateStr==='string' ? new Date(dateStr):dateStr; return date.toLocaleString();} function getValueClass(value,threshold=0){ return value>=threshold ? 'highlight-positive':'highlight-negative';} function getStatusBadge(status){const statusMap={'active':'status-active','signal':'status-signal','closed':'status-closed','executed':'status-active'};const statusClass=statusMap[status]||'status-active'; return `<span class="status ${statusClass}">${status.toUpperCase()}</span>`;} function getRankBadge(rank){ return `<span class="rank-badge">${rank}</span>`;} function getHeatmapCellClass(value){ if (value>=1){ return 'heatmap-good';} else if (value<0){ return 'heatmap-poor';} return 'heatmap-neutral';} function getValueColor(value,positiveThreshold=0){ if (value>positiveThreshold){ return DASHBOARD_CONFIG.COLORS.positive;} else if (value<positiveThreshold){ return DASHBOARD_CONFIG.COLORS.negative;} return DASHBOARD_CONFIG.COLORS.neutral;} function createColorArray(values,threshold=0){ return values.map(v=>getValueColor(v,threshold));} function getNestedValue(obj,path,defaultValue=null){ return path.split('.').reduce((current,prop)=>{ return current?.[prop]!==undefined ? current[prop]:defaultValue;},obj);} function showLoading(elementId){const element=document.getElementById(elementId); if (element){element.innerHTML='<div class="loading">Loading...</div>';}} function showError(elementId,message){const element=document.getElementById(elementId); if (element){element.innerHTML=`<div class="empty-state">‚ö†Ô∏è ${message}</div>`;}} function showEmptyState(elementId,icon='üì≠',message='No data available'){const element=document.getElementById(elementId); if (element){element.innerHTML=`<div class="empty-state"><div class="empty-state-icon">${icon}</div><p>${message}</p></div>`;}}